    }, [display]);
  const [display, setDisplay] = useState(false);
  const onClick = (e) => {
    e.preventDefault();
    setDisplay(true);
  };

  return (
    <div className="z-depth-1 checkout">
      <div className="row">
        <div className="col s12 m12 l12">
          <CheckOut
            basket={basket}
            onClick={onClick}
            display={display}
           
          />
          <div className="checkout-form">{display && <Checkout />}</div>
        </div>
      </div>
    </div>
  );
}

export default Basket;
