import { ADD_TO_BASKET, DELETE_BASKET_ITEM, GET_BASKET } from '../types';

export default (state, action) => {
  switch (action.type) {
    case ADD_TO_BASKET:
      return  {basket: action.payload}
    case GET_BASKET:
      return { ...state, basket: action.payload, total: action.payload.length !== 0 && action.payload.reduce((num,acc)=>num.price+acc )  };
    case DELETE_BASKET_ITEM:
      return {
        basket: state.basket.filter((item) => item._id !== action.payload),
      };
    default:
      return state;
  }
};
