import React, { Fragment, useEffect, useContext } from 'react';
import ReactStars from 'react-stars';
import AuthContext from '../../context/auth/authContext';
import ProductContext from '../../context/products/productContext';
import M from 'materialize-css';
function SingleItem({ match }) {
  const authContext = useContext(AuthContext);
  const productContext = useContext(ProductContext);
  const { isAuthenticated } = authContext;
  const { getProduct, product, loading, removeProduct } = productContext;
  let url = '';
  useEffect(() => {
    var elems = document.querySelectorAll('.carousel');
    M.Carousel.init(elems);
  }, []);
  useEffect(() => {}, []);

  useEffect(() => {
    getProduct(match.params.id);
    return () => {
      removeProduct();
    };
  }, []);

  return (
    <Fragment>
      <div className="container">
        <div className="row">
          <div className="col m12 s12 l12">
            <div className="carousel">
              <a className="carousel-item" href={url}>
                <img
                  src="https://miro.medium.com/max/800/0*a8__BlPyJgnS0bXa.jpg"
                  alt="Product"
                />
              </a>
              <a className="carousel-item" href={url}>
                <img
                  src="https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcT7D_QSKQpjw_aNe0O5TSiIwA54mAyA6dOqTA&usqp=CAU"
                  alt="Product"
                />
              </a>
              <a className="carousel-item" href={url}>
                <img
                  src="https://s.hdnux.com/photos/01/04/64/76/18042788/5/420x0.jpg"
                  alt="Product"
                />
              </a>
              <a className="carousel-item" href={url}>
                <img
                  src="https://cdn-a.william-reed.com/var/wrbm_gb_food_pharma/storage/images/publications/cosmetics/cosmeticsdesign-asia.com/headlines/market-trends/nail-polish-face-cream-and-perfumes-touted-as-attractive-sectors-for-halal-cosmetics-growth-in-2020/10397941-1-eng-GB/Nail-polish-face-cream-and-perfumes-touted-as-attractive-sectors-for-halal-cosmetics-growth-in-2020_wrbm_large.jpg"
                  alt="Product"
                />
              </a>
            </div>
          </div>
        </div>
      </div>

      <div className="container">
        <div className="row">
          <div className="col m12 s12 l12">
            <h2 className="header center">
              {product !== null && product[0].name}
            </h2>
            <div className="card horizontal">
              <div className="card-stacked">
                <div className="container">
                  <div className="row">
                    <div className="col m6 s6 l6">
                      <div className="card-content">
                        <p className="">{ product !== null&& product[0].description}</p>
                      </div>
                    </div>
                    <div className="col m3  mt-1 s3 ">
                      <div className="cord-content">
                        <span>Avarge rating:</span>
                        <ReactStars
                          count={5}
                          size={23}
                          color1={'#F6E8E0'}
                          color2={'#D84B59'}
                        />{' '}
                      </div>
                    </div>
                  </div>
                </div>
                <div className="container">
                  <div className="row">
                    <div className="col m4">
                      <div className="card-action">
                        <a href={url}>Add to basket</a>
                      </div>
                    </div>
                    <div className="col m4">
                      <div className="card-action">
                        <a href={url}>Add to wish list</a>
                      </div>
                    </div>
                    <div className="col m4">
                      <div className="card-action">
                        <a href={url}>Ask the seller</a>
                      </div>
                    </div>
                    {}
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </Fragment>
  );
}

export default SingleItem;
