import {
  REGISTER_SUCCESS,
  REGISTER_FAIL,
  UPDATE_DETAILS,
  UPDATE_DETAILS_FAIL,
  USER_LOADED,
  AUTH_ERROR,
  LOGIN_SUCCESS,
  LOGIN_FAIL,
  LOGOUT,
  EDIT_QUANTITY,
  CLEAR_ERRORS,
  ADD_TO_BASKET,
  BASKET_ERROR,
  DELETE_BASKET_ITEM,
} from '../types';

import React, { useReducer } from 'react';
import AuthContext from './authContext';
import authReducer from './authReducer';
import axios from 'axios';
axios.defaults.withCredentials = true;

const AuthState = (props) => {
  const initialState = {
    isAuthenticated: null,
    role: null,
    loading: true,
    error: null,
    user: null,
  };

  const [state, dispatch] = useReducer(authReducer, initialState);
  //Load user

  //Register user
  const register = async (formData) => {
    const config = {
      headers: {
        'Content-Type': 'application/json',
      },
    };
    try {
      const res = await axios.post('/api/v1/auth/register', formData, config);

      dispatch({ type: REGISTER_SUCCESS, payload: res.data.data });
    } catch (error) {
      console.log(error);
      dispatch({ type: REGISTER_FAIL, payload: error.response.data });
    }
  };
  //Login user
  const login = async (formData) => {
    const config = {
      headers: {
        'Content-Type': 'application/json',
      },
    };
    try {
      const res = await axios.post('/api/v1/auth/login', formData, config);

      dispatch({ type: LOGIN_SUCCESS, payload: res.data.data });
    } catch (error) {
      console.log(error);
      dispatch({ type: LOGIN_FAIL, payload: error.response.data });
    }
  };
  //Persistuser
  const persistUser = async () => {
    try {
      const res = await axios.get('/api/v1/auth/persistuser');

      dispatch({ type: LOGIN_SUCCESS, payload: res.data.data });
    } catch (error) {
      console.log(error);
      dispatch({ type: LOGIN_FAIL, payload: error.response.data });
    }
  };
  //Update user details
  const updateDetails = async (formData) => {
    const config = {
      headers: {
        'Content-Type': 'application/json',
      },
    };
    try {
      const res = await axios.put(
        '/api/v1/users/updatedetails',
        formData,
        config
      );

      dispatch({ type: UPDATE_DETAILS, payload: res.data.data });
    } catch (error) {
      console.log(error);
      dispatch({ type: UPDATE_DETAILS_FAIL, payload: error.response.data });
    }
  };
  ////////////////////////////////////////////////////////////////////////////////////////////////
  ////////  User Basket
  ////////////////////////////////////////////////////////////////////////////////////////////////
  //Edit user basket item quantity
  const editBasketItemQuantity = async (id, quantity) => {
    let data = {
      product: id,
      quantity: quantity,
    };
    console.log(data);
    const config = {
      headers: {
        'Content-Type': 'application/json',
      },
    };
    try {
      // await axios.put('/api/v1/basket/updatequantity', data, config);

      dispatch({ type: EDIT_QUANTITY, payload: data });
    } catch (error) {
      dispatch({ type: BASKET_ERROR, payload: error.response.data });
    }
  };
  //Add item to users' basket
  const addToBasket = async (product, quantity) => {
    let item = {
      product: product._id,
      quantity,
    };
    const config = {
      headers: {
        'Content-Type': 'application/json',
      },
    };

    try {
      await axios.put(
        `/api/v1/basket/${product._id}/addtobasket`,
        item,
        config
      );

      dispatch({ type: ADD_TO_BASKET, payload: item });
    } catch (error) {
      dispatch({ type: BASKET_ERROR, payload: error.response.data });
    }
  };

  //Delete product form basket
  const deleteBasketItem = async (product, quantity) => {
    let item = {
      product: product._id,
      quantity,
    };

    dispatch({ type: DELETE_BASKET_ITEM, payload: item });

    dispatch({ type: BASKET_ERROR, payload: error.response.data });
  };

  //Logout

  //Clear errors

  return (
    <AuthContext.Provider
      value={{
        isAuthenticated: state.isAuthenticated,
        loading: state.loading,
        user: state.user,
        error: state.error,
        register,
        login,
        updateDetails,
        persistUser,
        deleteBasketItem,
        editBasketItemQuantity,
        addToBasket,
        role: state.role,
      }}
    >
      {props.children}
    </AuthContext.Provider>
  );
};

export default AuthState;
