import React, { useContext, useEffect } from 'react';
import Stars from './starRating/Stars';
import ProductContext from '../../context/products/productContext';
function Options() {
  const productContext = useContext(ProductContext);
  const { filterByCategory, clearCurrentFilter, filterByRatings } = productContext;

  useEffect(() => {

  }, [clearCurrentFilter]);
  const onChange = (e) => {
    if (e.target.value !== 'showall') {
      filterByCategory(e.target.value);
    } else {
      clearCurrentFilter();
    }
  };

  return (
    <div className="col m2 s3 l2 mt-4 hide-on-small-and-down">
      <div className="ml-2">
        <p style={{ fontWeight: '700', fontSize: '1.2rem' }}>
          Avg. Customer rating{' '}
        </p>
        <Stars totalStars={5} size={20} />
        <span style={{ fontWeight: '700', fontSize: '1.2rem' }}> & up</span>

        <form action="#" className="mt-2">
          <p className="mb-1">
            <label>
              <input
                value="showall"
                className="with-gap"
                name="group1"
                type="radio"
                onChange={onChange}
              />
              <span>Show All</span>
            </label>
          </p>
          <p>
            <label>
              <input
                className="with-gap"
                name="group1"
                value="cosmetics"
                type="radio"
                onChange={onChange}
              />
              <span>Cosmetics</span>
            </label>
          </p>
          <p>
            <label>
              <input
                value="haircare"
                className="with-gap"
                name="group1"
                type="radio"
                onChange={onChange}
              />
              <span>Haircare</span>
            </label>
          </p>
          <p>
            <label>
              <input
                value="skincare"
                className="with-gap"
                name="group1"
                type="radio"
                onChange={onChange}
              />
              <span>Skincare</span>
            </label>
          </p>
        </form>
      </div>
    </div>
  );
}

export default Options;
