import React, { useEffect } from 'react';
import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
function Form() {
  const stripe = useStripe();

  return (
    <div className="container">
      <div className="row">
        <div className="col"></div>
      </div>
      <div className="row">
        <form className="col s8 offset-s2 m8 offset-m2 l12 l8 offset-l2">
          <div className="custom-form z-depth-3">
            {' '}
            <CardElement
              options={{
                style: {
                  base: {
                    width: '2px',
                    fontSize: '16px',
                    color: '#424770',
                    '::placeholder': {
                      color: '#aab7c4',
                    },
                  },
                  invalid: {
                    color: '#9e2146',
                  },
                },
              }}
            />
            <div className="button center">
              {' '}
              <button
                class="btn waves-effect waves-light"
                type="submit"
                name="action"
              >
                Pay
              </button>
            </div>{' '}
          </div>
        </form>
      </div>
    </div>
  );
}

export default Form;
