import React, { useEffect, useContext } from 'react';
import MainPageItemList from './MainPageItemList';
import Options from '../selector/Options';
import Pagination from '../utils/Pagination';
import AuthContext from '../../context/auth/authContext';
import ProductContext from '../../context/products/productContext';
function MainPageItems() {
  const authContext = useContext(AuthContext);
  const productContext = useContext(ProductContext);
  const { persistUser } = authContext;
  const { getProducts, products, loading } = productContext;
  useEffect(() => {
    persistUser();
    getProducts();
  }, []);

  return (
    <div className="z-depth-1 grey lighten-3">
      <div className="row">
        <Options />
        <div className="col s10 m9 l8">
          {products &&
            products.map((item) => (
              <MainPageItemList item={item} key={item.id} />
            ))}
        </div>

        <Pagination />
      </div>
    </div>
  );
}

export default MainPageItems;
