import {
  CREATE_PRODUCT,
  GET_PRODUCTS,
  PRODUCTS_FAIL,
  GET_PRODUCT,
  REMOVE_PRODUCT,
  FILTER_BY_CATEGORY,
  CLEAR_CURRENT_FILTER,
  FILTER_BY_USER_RATINGS,
} from '../types';

export default (state, action) => {
  switch (action.type) {
    case CREATE_PRODUCT:
      return {
        ...state,
        loading: false,
        error: null,
        product: action.payload,
      };
    case GET_PRODUCTS:
      return {
        ...state,
        loading: false,
        error: null,
        products: action.payload,
      };
    case GET_PRODUCT:
      return {
        ...state,
        product: state.products.filter(
          (product) => product._id === action.payload
        )[0],
        loading: false,
      };
    case REMOVE_PRODUCT:
      return {
        ...state,
        product: null,
        loading: true,
      };
    case PRODUCTS_FAIL:
      return {
        products: [],
        error: action.payload,
        loading: false,
      };
    case FILTER_BY_CATEGORY:
      return {
        ...state,
        filtered: state.products.filter
         
      };
    case FILTER_BY_USER_RATINGS:
      return {
        ...state,
        filtered:
          state.filtered.length !== 0
            ? state.filtered.filter(
                (item) => item.avarageRatings >= action.payload
              )
            : state.products.filter(
                (item) => item.avarageRatings === action.payload
              ),
      };

    case CLEAR_CURRENT_FILTER:
      return { ...state, filtered: [] };
    default:
      return state;
  }
};
