import { ADD_TO_BASKET } from '../types';

export default (state, action) => {
  switch (action.type) {
    case ADD_TO_BASKET:
      return { ...state, basket: action.payload.basket };
    case GET_BASKET:
    return { ...sate, basket: action.payload.basket };

    default:
      return state;
  }
};
