import React, { Fragment, useEffect, useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import ReactStars from 'react-stars';
import AuthContext from '../../../context/auth/authContext';
import ProductContext from '../../../context/products/productContext';
import AlertContext from '../../../context/alert/alertContext';
import BasketContext from '../../../context/basket/basketContext';
import M from 'materialize-css';
import AddImages from '../../admin/AddImages';
function SingleItem({ match }) {
  const alertContext = useContext(AlertContext);
  const basketContext = useContext(BasketContext);
  const {addToBasket} = bask
  const { setAlert } = alertContext;
  const authContext = useContext(AuthContext);
  const productContext = useContext(ProductContext);
  const { role } = authContext;
  const {
    getProduct,
    product,
    removeProduct,
    loading,
    addImages,
  } = productContext;

  let url = '';
  useEffect(() => {
    const options = { numVisible: 5 };
    var elems = document.querySelectorAll('.carousel');
    M.Carousel.init(elems, options);
  }, []);
  const [image, setImage] = useState(false);

  useEffect(() => {
    getProduct(match.params.id);
    return () => {
      removeProduct();
    };
  }, []);
  useEffect(() => {
    setImage(false);
  }, [addImages]);
  const showImageInput = () => {
    setImage(true);
  };
  const toBasket = (e) => {
    e.preventDefault();
    setAlert(
      'Item succesfully added to basket',
      'green',
      'col s8 offset-s2 m8 offset-m2 l8 offset-l2'
    );
    addToBasket(match.params.id);
  };
  return (
    <Fragment>
      <div className="container">
        <div className="row">
          <div className="col m12 s12 l12">
            <div className="carousel">
              <a class="carousel-item" href={url}>
                <img src={product?.photo[0]} />
              </a>
              <a class="carousel-item" href={url}>
                <img src={product?.photo[1]} />
              </a>
              <a class="carousel-item" href={url}>
                <img src={product?.photo[2]} />
              </a>

              {!product?.photo && (
                <a className="carousel-item" href={url}>
                  <img
                    src="https://1080motion.com/wp-content/uploads/2018/06/NoImageFound.jpg.png"
                    alt="Product"
                  />
                </a>
              )}
            </div>
          </div>
        </div>
      </div>
      {image && <AddImages id={match.params.id} />}

      <div className="container">
        <div className="row">
          <div className="col m12 s12 l12">
            <h2 className="header center">{product && product.name}</h2>
            <div className="card horizontal">
              <div className="card-stacked">
                <div className="container">
                  <div className="row">
                    <div className="col m6 s6 l6">
                      <div className="card-content">
                        <p className="">{product && product.description}</p>
                      </div>
                    </div>
                    <div className="col m3  mt-1 s3 ">
                      <div className="cord-content">
                        <span>Avarge rating:</span>
                        <ReactStars
                          count={5}
                          size={23}
                          color1={'#F6E8E0'}
                          color2={'#D84B59'}
                        />{' '}
                      </div>
                    </div>
                  </div>
                </div>
                <div className="container">
                  <div className="row">
                    <div className="col m3">
                      <div className="card-action">
                        <a href="#one!" onClick={toBasket}>
                          Add to basket
                        </a>
                      </div>
                    </div>
                    <div className="col m3">
                      <div className="card-action">
                        <a href={url}>Add to wish list</a>
                      </div>
                    </div>
                    <div className="col m3">
                      <div className="card-action">
                        <a href={url}>Ask the seller</a>
                      </div>
                    </div>
                    {role === 'admin' && (
                      <div className="col m3">
                        <div className="card-action">
                          <div className="row">
                            <div className="input-field col s4 m4 l4 offset-s1 offset-m1 offset-l1">
                              <button
                                className="btn waves-effect waves-light blue lighten-1"
                                onClick={showImageInput}
                              >
                                Add Images
                                <i className="large material-icons right">
                                  send
                                </i>
                              </button>
                            </div>
                          </div>{' '}
                        </div>
                      </div>
                    )}
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </Fragment>
  );
}

export default SingleItem;
