import {
  CREATE_PRODUCT,
  GET_PRODUCTS,
  PRODUCTS_FAIL,
  GET_PRODUCT,
} from '../types';

export default (state, action) => {
  switch (action.type) {
    case CREATE_PRODUCT:
      return {
        ...state,
        loading: false,
        error: null,
        products: action.payload,
      };
    case GET_PRODUCTS:
      return {
        ...state,
        loading: false,
        error: null,
        products: action.payload,
      };
    case GET_PRODUCT:
      return {
        ...state,
        product: state.products.filter(
          (product) => product._id === action.payload
        ),
        loading: false,
      };
    case REMOVE:
      return {
        ...state,
        product: state.products.filter(
          (product) => product._id === action.payload
        ),
        loading: false,
      };
    case PRODUCTS_FAIL:
      return {
        products: [],
        error: action.payload,
        loading: false,
      };
    default:
      return state;
  }
};
