import React, { useEffect, useState, useContext } from 'react';
import BasketItem from '../item/BasketItem';
import Total from './Total';
import AlertContext from '../../../context/alert/alertContext';
const CheckOut = ({ basket }) => {
  const alertContext = useContext(AlertContext);
  const { setAlert } = alertContext;
  const [quantity, setQuantity] = useState(1);
  useEffect(() => {
    if (quantity === 0) {
      setQuantity(1);
      setAlert(
        'Quantity can not be set to 0',
        'blue darken-2',
        'col s10 offset-s1 m10 offset-m1 l10 offset-l1'
      );
    }
    console.log(quantity);
  }, [quantity]);
  const decreaseQuantity = () => {
    setQuantity(quantity - 1);
  };
  const increaseQuantity = () => {
    setQuantity(quantity + 1);
  };
  return (
    <div className="card inner-checkout">
      <div className="card-content white-text">
        <span className="card-title center section">Basket Overview</span>
        <p className="center mt-4">
          I am a very simple card. I am good at containing small bits of
          information. I am convenient because I require little markup to use
          effectively.
        </p>{' '}
        <div className="section mt-4">
          {' '}
          {basket?.[0] &&
            basket.map((basket, index) => (
              <BasketItem
                basket={basket}
                key={index}
                increaseQuantity={increaseQuantity}
                decreaseQuantity={decreaseQuantity}
                price={basket.price *q}
                quantity={quantity}
              />
            ))}
        </div>
      </div>
      <Total />
    </div>
  );
};

export default CheckOut;
