import React from 'react';
import { CardElement, CardNumberElement } from '@stripe/react-stripe-js';
function Form() {
  return (
    <div className="container">
      <div className="row">
        <div className="col s6 m6 l6" style={{ position: 'absolute' }}>
          <CardElement
            options={{
              style: {
                base: {
                  left: '50px',
                  position: 'relative',
                  fontSize: '16px',
                  color: '#424770',
                  '::placeholder': {
                    color: '#aab7c4',
                  },
                },
                invalid: {
                  color: '#9e2146',
                },
              },
            }}
          />
        </div>
      </div>
    </div>
  );
}

export default Form;
