import {
  CREATE_PRODUCT,
  GET_PRODUCTS,
  PRODUCTS_FAIL,
  GET_PRODUCT,
  REMOVE_PRODUCT
} from '../types';

import React, { useReducer } from 'react';
import ProductContext from './productContext';
import productReducer from './productReducer';
import axios from 'axios';
axios.defaults.withCredentials = true;

const ProductState = (props) => {
  const initialState = {
    products: [],
    product: null,
    loading: false,
    error: null,
  };

  const [state, dispatch] = useReducer(productReducer, initialState);

  //Create products
  const createProduct = async (formData) => {
    const config = {
      headers: {
        'Content-Type': 'application/json',
      },
    };
    console.log(formData);
    try {
      const res = await axios.post('/api/v1/admin/products', formData, config);

      dispatch({ type: CREATE_PRODUCT, payload: res.data.data });
    } catch (error) {
      console.log(error);
      // dispatch({ type: UPDATE_DETAILS_FAIL, payload: error.response.data });
    }
  };
  //Create products
  const addImages = async (formData) => {
    const config = {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    };
    console.log(formData);
    try {
      const res = await axios.post('/api/v1/admin/products/addimages', formData, config);

      dispatch({ type: CREATE_PRODUCT, payload: res.data.data });
    } catch (error) {
      console.log(error);
      // dispatch({ type: UPDATE_DETAILS_FAIL, payload: error.response.data });
    }
  };
  //Create products
  const getProducts = async () => {
    try {
      const res = await axios.get('/api/v1/products');

      dispatch({ type: GET_PRODUCTS, payload: res.data.data });
    } catch (error) {
      console.log(error);
      dispatch({ type: PRODUCTS_FAIL, payload: error.response });
    }
  };

  //Get single product
  const getProduct = async (id) => {
    dispatch({ type: GET_PRODUCT, payload: id });
  };
  //Remove product from state
  const removeProduct = async (id) => {
    dispatch({ type: REMOVE_PRODUCT });
  };

  return (
    <ProductContext.Provider
      value={{
        products: state.products,
        loading: state.loading,
        product: state.product,
        error: state.error,
        createProduct,
        getProducts,
        getProduct,
        removeProduct,
      }}
    >
      {props.children}
    </ProductContext.Provider>
  );
};

export default ProductState;
