import React, { useContext, useState } from 'react';
import AuthContext from '../../../context/auth/authContext';
import AlertContext from '../../../context/alert/alertContext';
export default function EditDeliveryAddress({
  toggleForm = (f) => f,
  location,
}) {
  const authContext = useContext(AuthContext);
  const alertContext = useContext(AlertContext);
  const { setAlert } = alertContext;
  const {
    updateUserAddress,
    user: { address },
  } = authContext;
  const [updateAddress, setUpdateAddress] = useState({
    line1: address?.[location]?.line1 || '',
    line2: address?.[location]?.line2 || '',
    city: address?.[location]?.city || '',
    postcode: address?.[location]?.postcode || '',
  });
  const { line1, line2, city, postcode } = updateAddress;

  const onSubmit = (e) => {
    e.preventDefault();

    if (line1 === '' || city === '' || postcode === '') {
      setAlert(
        'Please provide all required details',
        'red',
        'col s8 offset-s2 m8 offset-m2 l8 offset-l2'
        );
        to
    } else {
      updateUserAddress(updateAddress, location);
      toggleForm();
    }
  };
  const onChange = (e) => {
    setUpdateAddress({ ...updateAddress, [e.target.name]: e.target.value });
  };
  return (
    <div className="row">
      <form className="col s12">
        <div className="row">
          <div className="input-field col s6">
            <i className="material-icons prefix">home</i>
            <input
              id="first_name"
              type="text"
              className="validate"
              onChange={onChange}
              value={line1}
              name="line1"
            />
            <label htmlFor="first_name" className="active">
              Address line 1
            </label>
          </div>
          <div className="input-field col s6">
            <i className="material-icons prefix">home</i>
            <input
              id="last_name"
              type="text"
              className="validate"
              onChange={onChange}
              value={line2}
              name="line2"
            />
            <label htmlFor="last_name" className="active">
              Address line 2 (optional)
            </label>
          </div>
          <div className="input-field col s6">
            <i className="material-icons prefix">location_city</i>
            <input
              id="email"
              type="email"
              className="validate"
              onChange={onChange}
              value={city}
              name="city"
            />
            <label htmlFor="email" className="active">
              Town/City
            </label>
          </div>
          <div className="input-field col s6">
            <i className="material-icons prefix">location_on</i>
            <input
              id="icon_telephone"
              type="tel"
              className="validate"
              onChange={onChange}
              value={postcode}
              name="postcode"
            />
            <label htmlFor="icon_telephone" className="active">
              Postcode
            </label>
          </div>
        </div>

        <button
          className="btn waves-effect waves-light blue lighten-1"
          type="submit"
          onClick={onSubmit}
        >
          Submit
          <i className="large material-icons right ">send</i>
        </button>
      </form>
    </div>
  );
}
