import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import BasketItem from './components/BasketItem';
const CheckOut = ({ basket }) => {
  

  return (
    <div className="card">
      <div class="card blue-grey darken-1">
        <div class="card-content white-text">
          <span class="card-title center mt-2">Basket Overview</span>
          <p className="center mt-2">
            I am a very simple card. I am good at containing small bits of
            information. I am convenient because I require little markup to use
            effectively.
          </p>{' '}
          <div className="row">
            <div className="col">
              <div className="col s12 m8 offset-m2 l8 offset-l2 mt-4">
                {basket &&
                  basket.map((basket, index) => (
                    <BasketItem basket={basket} key={index} />
                  ))}
              </div>
            </div>
          </div>
        </div>

        <div class="card-action basket">
          <div>
            <h5 className="white-text">Total: {basket?.[0].price}</h5>
          </div>
          <Link href="/checkout">Proceed to Checkout</Link>
        </div>
      </div>
    </div>
  );
};

export default CheckOut;
