import {
  REGISTER_SUCCESS,
  REGISTER_FAIL,
  UPDATE_DETAILS,
  LOGIN_SUCCESS,
  LOGIN_FAIL,
  LOGOUT,
  CLEAR_ERRORS,
  USER_ERROR,
  ADD_ADDRESS,
  UPDATE_ADDRESS,
  DELETE_ADDRESS,
  AUTH_FAIL,
} from '../types';

export default (state, action) => {
  switch (action.type) {
    //////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
    //////////// User actions
    //////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
    case UPDATE_DETAILS:
      return {
        ...state,
        isAuthenticated: true,
        loading: false,
        user: action.payload,
        error: null,
      };
    case UPDATE_ADDRESS:
      return {
        ...state,
        user: {
          ...state.user,
          address: state.user.address.map((address) =>
            address._id === action.payload._id ? action.payload : address
          ),
        },
      };
    case ADD_ADDRESS:
      return {
        ...state,
        user: { ...state.user, address: action.payload },
      };
    case DELETE_ADDRESS:
      return {
        ...state,
        user: {
          ...state.user,
          address: state.user.address.filter(
            (item) => item._id !== action.payload
          ),
        },
      };

    case USER_ERROR:
      return {
        ...state,
        isAuthenticated: true,
        loading: false,
        error: action.payload,
      };
    //////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
    //////////// Authorization
    //////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
    case REGISTER_SUCCESS:
      return {
        ...state,
        isAuthenticated: true,
        loading: false,
        user: action.payload,

        error: null,
      };
    case LOGIN_SUCCESS:
      return {
        ...state,
        isAuthenticated: true,
        loading: false,
        user: action.payload,
        authError: null,
      };
    case AUTH_FAIL:
      return {
        ...state,
        isAuthenticated: false,
        loading: false,
        user: null,
        authError: action.payload,
      };

    case LOGOUT:
      return {
        ...state,
        isAuthenticated: false,
        user: null,
      };
    case CLEAR_ERRORS:
      return { ...state, authError: null };

    default:
      return state;
  }
};
