import React, { useContext, useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import BasketContext from '../../../context/basket/basketContext';
import QuantitySelector from './QuantitySelector';
import AlertContext from '../../../context/alert/alertContext';
function BasketItem({
  basket: { name, description, _id, photo },

  price,
}) {
  const alertContext = useContext(AlertContext);
  const { setAlert } = alertContext;
  const [quantity, setQuantity] = useState(1);
  useEffect(() => {
    if (quantity === 0) {
      setQuantity(1);
      setAlert(
        'Quantity can not be set to 0',
        'blue darken-2',
        'col s10 offset-s1 m10 offset-m1 l10 offset-l1'
      );
    }
    console.log(quantity);
  }, [quantity]);
  const decreaseQuantity = () => {
    setQuantity(quantity - 1);
  };
  const increaseQuantity = () => {
    setQuantity(quantity + 1);
  };
  let url = '';
  const basketContext = useContext(BasketContext);

  const { deleteBasketItem } = basketContext;
  const deleteItem = (e) => {
    e.preventDefault();
    deleteBasketItem(_id);
  };
  return (
    <div className="row">
      <div className="col m8 l8 s10 offset-l2 offset-m2 offset-s1">
        <div className="card horizontal-custom-item-card">
          <div className="card-image-item-card">
            <img src={photo?.[0]} className="img-custom-item-card basket" />
          </div>{' '}
          <div className="custom-item-card-container basket">
            {' '}
            <div className="row">
              <div className="col m12 s12 l12">
                <div className="basket-header">
                  <h6 className="basket-header_text center-align">{name}</h6>{' '}
                  <p className="custom-item-card-ellipsis basket">
                    {description}
                  </p>{' '}
                  <div className="basket-total-price">
                    Item total: £ {price}
                  </div>
                </div>
              </div>
            </div>
            <div className="row">
              <div className="col m12 s12 l12">
                <div className="card-action">
                  <div>
                    {' '}
                    <Link to={`/item/${_id}`}>Item Details</Link>
                  </div>{' '}
                  <div>
                    {' '}
                    <Link to={'/basket'} onClick={deleteItem}>
                      Delete
                    </Link>{' '}
                  </div>
                  <div>
                    {' '}
                    <QuantitySelector
                      price={price}
                      increaseQuantity={increaseQuantity}
                      decreaseQuantity={decreaseQuantity}
                      quantity={quantity}
                    />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default BasketItem;
