import React, { useReducer, useState } from 'react';
import EditDeliveryAddress from './EditDeliveryAddress';
function DeliveryAddress({ address }) {
  const [form, toggleForm] = useReducer((form) => !form, false);
  let editAddress;
  let type;
  const setAddressKey = (e) => {
    toggleForm();
    if (e.target.name === 'primary') {
      editAddress = address.primaryAddress;
      type = 'primaryAddress';
    } else {
      editAddress = address.secondaryAddress;
      type = 'secondaryAddress';
    }
  };

  return (
    <div id="test6">
      {form ? (
        <EditDeliveryAddress
          toggleForm={toggleForm}
          address={editAddress}
          type={type}
        />
      ) : (
        <div className="row">
          <div className="col s8 offset-s2 m6 l6">
            <div className="card-panel grey lighten-3">
              <span className="black-text">
                <table>
                  <thead>
                    <tr>
                      <th>Primary</th>
                    </tr>
                  </thead>

                  <tbody>
                    <tr>
                      <td>
                        {address.primaryAddress
                          ? address.primaryAddress.line1
                          : 'In order to expedite the checkout process please set up the primary delivery address'}
                      </td>
                    </tr>
                  </tbody>
                </table>
              </span>{' '}
              <button
                className="btn waves-effect waves-light light-blue mt-3"
                onClick={setAddressKey}
                name="primary"
              >
                Edit
                <i className="material-icons right">edit</i>
              </button>
            </div>
          </div>
          <div className="col s8 offset-s2 m6 l6">
            <div className="card-panel grey lighten-4">
              <span className="black-text">
                <table>
                  <thead>
                    <tr>
                      <th>Secondary</th>
                    </tr>
                  </thead>

                  <tbody>
                    <tr>
                      <td>
                        {address.secondaryAddress
                          ? address.primaryAddress.line1
                          : 'No secondary address provided'}
                      </td>
                    </tr>
                  </tbody>
                </table>
              </span>
              <button
                className="btn waves-effect waves-light light-blue mt-3"
                onClick={setAddressKey}
                name="secondary"
              >
                Edit
                <i className="material-icons right">edit</i>
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default DeliveryAddress;
