import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import M from 'materialize-css';
import PersonalDetails from './PersonalDetails';
import PaymentCards from './PaymentCards';
function Account() {
  useEffect(() => {
    var el = document.querySelector('.tabs');
    M.Tabs.init(el);
  }, []);
  const [tab, setTab] = useState('');

  let url = ' ';
  const onClick = (e) => {
    setTab('');
    setTab(e.target.name);
    console.log(tab);
  };
  return (
    <div className="row">
      <div className="col s12 m12 l12">
        <div className="card">
          <div className="card-content">
            <p>
              I am a very simple card. I am good at containing small bits of
              information. I am convenient because I require little markup to
              use effectively.
            </p>
          </div>
          <div className="card-tabs">
            <ul className="tabs tabs-fixed-width">
              <li className="tab" >
                <Link to="" onClick={onClick} name="personaldetails">
                  Personal Details
                </Link>
              </li>
              <li className="tab">
                <Link to="" onClick={onClick} name="paymentcards">
                  Payment Cards
                </Link>
              </li>
              <li className="tab">
                <Link to="" onClick={onClick} name="deliveryaddress">
                  Delivery Addresses
                </Link>
              </li>
            </ul>
          </div>
          <div className="card-content grey lighten-4">
            {tab === 'personaldetails' && <PersonalDetails />}
            {tab === 'paymentcards' && <PaymentCards />}
            <a
              href={url}
              className="waves-effect waves-light btn orange lighten-2"
            >
              <i className="material-icons right">mode_edit</i>Edit
            </a>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Account;
