import React, { useContext, useState } from 'react';
import ReactStars from 'react-stars';
import ProductContext from '../../context/products/productContext';
function Options() {
  const productContext = useContext(ProductContext);
  const { filterByCategory } = productContext;
  const [category, setCategory] = useState({
    cosmetics: '',
    skincare: '',
    haircare: '',
  });
  const { cosmetics, skincare, haircare } = category;
  const onChange = (e) => {
    setCategory({ ...category, [e.target.name]: e.target.value });
    filterByCategory(e.target.value);
  };
  return (
    <div className="col m2 s3 l2 mt-4 hide-on-small-and-down">
      <div className="ml-2">
        <p style={{ fontWeight: '700', fontSize: '1.2rem' }}>
          Avg. Customer rating{' '}
        </p>
        <ReactStars count={5} size={23} color1={'grey'} color2={'#D84B59'} />{' '}
        <span style={{ fontWeight: '700', fontSize: '1.2rem' }}> & up</span>
        <form action="#" className="mt-2">
          <p>
            <label>
              <input
                id="indeterminate-checkbox"
                type="checkbox"
                name="cosmetics"
                value="cosmetics"
                onSelect={onChange}
                defaultChecked={selected}
              />
              <span>Cosmetics</span>
            </label>
          </p>
          <p>
            <label>
              <input
                id="indeterminate-checkbox"
                type="checkbox"
                name="haircare"
                value="haircare"
                onChange={onChange}
              />
              <span>Haircare</span>
            </label>
          </p>
          <p>
            <label>
              <input
                id="indeterminate-checkbox options"
                type="checkbox"
                name="skincare"
                value="skincare"
                onChange={onChange}
              />
              <span>Skincare</span>
            </label>
          </p>
        </form>
      </div>
    </div>
  );
}

export default Options;
