import { CREATE_PRODUCT, GET_PRODUCTS, PRODUCTS_FAIL } from '../types';

export default (state, action) => {
  switch (action.type) {
    case CREATE_PRODUCT:
      return {
        ...state,
        loading: false,
        error: null,
        products: action.payload,
      };
    case GET_PRODUCTS:
      return {
        ...state,
        loading: false,
        error: null,
        products: action.payload,
      };
    case PRODUCTS_FAIL:
    default:
      return state;
  }
};
