import React, { useState, Fragment } from 'react';
import EditDetails from './EditDetails';
import DisplayDetails from './DisplayDetails';
function PersonalDetails() {
  const [edit, setEdit] = useState(false);
  const onClick = (e) => {
    e.preventDefault();
    setEdit(!edit);
  };
  
  let url = '';
  return (
    <Fragment>
      {edit ? <EditDetails /> : <DisplayDetails />}
      <div>
        {' '}
        {!edit ? (
          <a
            href={url}
            className="waves-effect waves-light btn orange lighten-2"
            onClick={onClick}
          >
            <i className="material-icons right">mode_edit</i>Edit
          </a>
        ) : (
          <a
            href={url}
            className="waves-effect waves-light btn orange lighten-2"
            onClick={onSubmit}
          >
            <i className="material-icons right">mode_edit</i>Submit
          </a>
        )}
      </div>
    </Fragment>
  );
}

export default PersonalDetails;
