import { useState } from 'react';
import React from 'react';
import {
  CardNumberElement,
  CardExpiryElement,
  CardCvcElement,
  useStripe,
  useElements,
} from '@stripe/react-stripe-js';

const CheckoutForm = ({ price, onSuccessfulCheckout }) => {
  const [isProcessing, setProcessingTo] = useState(false);
  const [checkoutError, setCheckoutError] = useState();

  const stripe = useStripe();
  const elements = useElements();

  // TIP
  // use the cardElements onChange prop to add a handler
  // for setting any errors:

  const handleCardDetailsChange = (ev) => {
    ev.error ? setCheckoutError(ev.error.message) : setCheckoutError();
  };

  const handleFormSubmit = async (ev) => {};

  // Learning
  // A common ask/bug that users run into is:
  // How do you change the color of the card element input text?
  // How do you change the font-size of the card element input text?
  // How do you change the placeholder color?
  // The answer to all of the above is to use the `style` option.
  // It's common to hear users confused why the card element appears impervious
  // to all their styles. No matter what classes they add to the parent element
  // nothing within the card element seems to change. The reason for this is that
  // the card element is housed within an iframe and:
  // > styles do not cascade from a parent window down into its iframes

  const iframeStyles = {
    base: {
      color: '#fff',
      fontSize: '16px',
      iconColor: '#fff',
    },
    invalid: {
      iconColor: 'black',
      color: '#FFC7EE',
    },
    complete: {
      iconColor: '#cbf4c9',
    },
  };

  const cardElementOpts = {
    iconStyle: 'default',
    style: iframeStyles,
    hidePostalCode: true,
  };

  return (
    <div className="row">
      {' '}
      <form className="col s12 m12 l12" onSubmit={handleFormSubmit}>
        <div className="row">
          <div className="col s12 m12 l12">
            {' '}
            <label htmlFor="cardnumber" className="payment-cells-label">
              CARD NUMBER
            </label>
            <div className="payment-cells">
              {' '}
              <CardNumberElement
                name="cardnumber"
                options={cardElementOpts}
                onChange={handleCardDetailsChange}
              />
            </div>
          </div>
        </div>
        <div className="row">
          <div className="col s6 m6 l6">
            <label htmlFor="cardexpiry" className="payment-cells-label">
              CARD EXPIRATION DATE
            </label>
            <div className="payment-cells">
              {' '}
              <CardExpiryElement name="cardexpiry" />{' '}
            </div>
          </div>
          <div className="col s4 offset-s2 m4 offset-m2 l4 offset-l2">
            <label htmlFor="cardcvc" className="payment-cells-label">
              CV CODE
            </label>
            <div className="payment-cells">
              {' '}
              <CardCvcElement name="cardcvc" />
            </div>
          </div>
        </div>
        <div className="row">
          <div className="col s12 m12 l12">
            {' '}
            <label htmlFor="discount" className="payment-cells-label">
              CARD NUMBER
            </label>
            <div className="payment-cells">
              <input type="text" name="discount" id="" />
            </div>
          </div>
        </div>
      </form>
    </div>
  );
};

export default CheckoutForm;
