import React, { useContext,  useEffect } from 'react';
import { Link } from 'react-router-dom';
import BasketContext from '../../context/basket/basketContext';
function BasketItem({
  item: { name, description, quantity, _id, sold, price },
}) {
  useEffect
  let url = '';
  const basketContext = useContext(BasketContext);
  const { deleteBasketItem, basket } = basketContext;
  const onClick = () => {
    deleteBasketItem(_id);
  };
  return (
    <div className="container">
      <div className="row">
        <div className="col s12 m12 l12">
          <h3 className="header" style={{}}>
            {name}
          </h3>
          <div className="card horizontal z-depth-4">
            <div className="card-image">
              <img
                src="https://surlybikes.com/uploads/bikes/_medium_image/KarateMonkey_BK2042-2000x1333.jpg"
                alt="Product"
              />
            </div>
            <div className="card-stacked">
              <div className="card-content">
                <p>{description}</p>
              </div>
              <div className="waves-effect waves-yellow card-action">
                <Link to={`item/${_id}`}>View item details</Link>
              </div>
              <div className="waves-effect waves-red card-action">
                <Link href={url} onClick={onClick}>
                  Delete
                </Link>
              </div>
              <div className="waves-effect waves-purple card-action">
                <a href={url}>Checkout</a>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default BasketItem;
