import React, { useEffect } from 'react';
import M from 'materialize-css';
function AddMerchandise() {
  useEffect(() => {
    const elem = document.getElementById('textarea2');
     M.CharacterCounter.init(elem);
  }, []);
  return (
    <div className="row">
      <form className="col s12">
 
        <div className="row">
          <div className="input-field col s12">
            <textarea
              id="textarea2"
              className="materialize-textarea"
              data-length="120"
            ></textarea>
            <label htmlFor="textarea2">Description</label>
          </div>
        </div> <div class="file-field input-field">
      <div class="btn">
        <span>File</span>
        <input type="file" multiple/>
      </div>
      <div class="file-path-wrapper">
        <input class="file-path validate" type="text" placeholder="Upload one or more files"/>
      </div>
    </div>
      </form>
    </div>
  );
}

export default AddMerchandise;
