import {
  CREATE_PRODUCT,
  GET_PRODUCTS,
  PRODUCTS_FAIL,
  GET_PRODUCT,
  REMOVE_PRODUCT,
  SORT_BY_CATEGORY,
} from '../types';

export default (state, action) => {
  switch (action.type) {
    case CREATE_PRODUCT:
      return {
        ...state,
        loading: false,
        error: null,
        product: action.payload,
      };
    case GET_PRODUCTS:
      return {
        ...state,
        loading: false,
        error: null,
        products: action.payload,
      };
    case GET_PRODUCT:
      return {
        ...state,
        product: state.products.filter(
          (product) => product._id === action.payload
        )[0],
        loading: false,
      };
    case REMOVE_PRODUCT:
      return {
        ...state,
        product: null,
        loading: true,
      };
    case PRODUCTS_FAIL:
      return {
        products: [],
        error: action.payload,
        loading: false,
      };
    case SORT_BY_CATEGORY:
      
    default:
      return state;
  }
};
