import React, { useContext, Fragment } from 'react';
import { Link } from 'react-router-dom';
import AuthContext from '../../context/auth/authContext';
import ProductContext from '../../context/products/productContext';
function Navbar() {
  const authContext = useContext(AuthContext);
  const { isAuthenticated, user, role } = authContext;
  const productContext = useContext(ProductContext);
  const { getProducts} = productContext;

  let url = ' ';
  const authLinks = (
    <Fragment>
      <li>Hello {user && user.name}</li>
      <li>
        <Link to="/account">Account</Link>
      </li>
      <li>
        <Link to="/orders">Orders</Link>
      </li>
      <li>
        <Link to="/basket">Basket</Link>
      </li>{' '}
      <li>
        <Link to="/basket">Logout</Link>
      </li>
    </Fragment>
  );
  const adminLinks = (
    <Fragment>
      <li>
        <Link to="/admin/addmerchandise">Add Product</Link>
      </li>
    </Fragment>
  );

  const guestLinks = (
    <Fragment>
      <li>
        <Link to="/register">Register</Link>
      </li>
      <li>
        <Link to="/login">Login</Link>
      </li>
    </Fragment>
  );

  return (
    <Fragment>
      <nav className="blue-grey lighten-2">
        <div className="nav-wrapper">
          <Link to="/" className="brand-logo">
            React<i className="fas fa-box-open hide-on-small-and-down ml-1"></i>
            Pantry
          </Link>

          <a href={url} data-target="mobile-demo" className="sidenav-trigger">
            <i className="material-icons">menu</i>
          </a>

          <ul className="right hide-on-med-and-down">
            {user?.role === 'admin' && adminLinks}
            {isAuthenticated ? authLinks : guestLinks}
          </ul>
        </div>
      </nav>
      <ul className="sidenav" id="mobile-demo">
        <li>
          <a href={url}>
            <i className="material-icons  hello-user ">account_circle</i>
            <p className="hello-user-name ">Hello, Evalds</p>
          </a>
        </li>
        <li>
          <div className="divider"></div>
        </li>
        {isAuthenticated ? authLinks : guestLinks}
      </ul>
    </Fragment>
  );
}

export default Navbar;
