import React from 'react';
import { Link } from 'react-router-dom';
function MainPageItemList({ item }) {
  return (
    <div className="container">
      <div className="row">
        <div className="col push-m1 s12 m12 push-s1 l12 push-l1 mt-2">
          <div
            className="card horizontal hoverable"
            style={{ maxHeight: '200px' }}
          >
            <div className="card-image" style={{ overflow: 'hidden' }}>
              <img src={item.photo[0]} alt="Product" />
            </div>
            <div className="card-stacked">
              <h5 className="header center" style={{}}>
                {item.name}
              </h5>
              <div className="card-content">
                <p>{item.description}</p>
              </div>{' '}
              <div className="card-action">
                <Link to={`/item/${item._id}`}>More</Link>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default MainPageItemList;
