import React, { useContext, useState } from 'react';
import ReactStars from 'react-stars';
import ProductContext from '../../context/products/productContext';
function Options() {
  let starCount = 5;
  const productContext = useContext(ProductContext);
  const { filterByCategory, category, clearCategories } = productContext;
  const [categoryFilter, setCategoryFilter] = useState({
    comsetics: false,
    haircare: false,
    skincare: false,
  });
  const [stars, setStars] = useState(null);
  const { cosmetics, skincare, haircare } = categoryFilter;
  const onChange = (e) => {
    setCategoryFilter({ ...categoryFilter, [e.target.name]: !e.target.value });
  };
  const searchRating = (e) => {
    console.log('Changed');
  };
  return (
    <div className="col m2 s3 l2 mt-4 hide-on-small-and-down">
      <div className="ml-2">
        <p style={{ fontWeight: '700', fontSize: '1.2rem' }}>
          Avg. Customer rating{' '}
        </p>
        <ReactStars
          count={5}
          size={23}
          half={false}
          color1={'grey'}
          color2={'#D84B59'}
          onChange={searchRating}
        />{' '}
        <span style={{ fontWeight: '700', fontSize: '1.2rem' }}> & up</span>
        <form action="#" className="mt-2">
          <p>
            <label>
              <input
                id="indeterminate-checkbox"
                type="checkbox"
                name="cosmetics"
                value={cosmetics}
                onClick={onChange}
              />
              <span>Cosmetics</span>
            </label>
          </p>
          <p>
            <label>
              <input
                id="indeterminate-checkbox"
                type="checkbox"
                name="haircare"
                value={haircare}
                onClick={onChange}
              />
              <span>Haircare</span>
            </label>
          </p>
          <p>
            <label>
              <input
                id="indeterminate-checkbox options"
                type="checkbox"
                name="skincare"
                value={skincare}
                onChange={onChange}
              />
              <span>Skincare</span>
            </label>
          </p>
        </form>
      </div>
    </div>
  );
}

export default Options;
