import React, { useReducer, useState } from 'react';
import EditDeliveryAddress from './EditDeliveryAddress';
import AdressCard from './AddressCard';
function DeliveryAddress({ address }) {
  const [form, toggleForm] = useReducer((form) => !form, false);

  const editAddress = (e) => {
    toggleForm();
  };

  return (
    <div id="test6">
      {form ? (
        <EditDeliveryAddress toggleForm={toggleForm} address={address} />
      ) : (
        <div className="row">
          {address.map((item, index) => (
            <AdressCard address={item} key={index} editAddress={editAddress} />
          ))}
        </div>
      )}
    </div>
  );
}

export default DeliveryAddress;
