import React, { useContext, useState } from 'react';
import Stars from './starRating/Stars';
import ProductContext from '../../context/products/productContext';
function Options() {
  const productContext = useContext(ProductContext);
  const { filterByCategory, category, clearCategories } = productContext;
  const [categoryFilter, setCategoryFilter] = useState('');

  const onChange = (e) => {
    if (e.target.checked === 'checked') {
      clearCategories();
    } else  {
    
      setCategoryFilter(e.target.name);
      filterByCategory(categoryFilter);
    }
  };

  return (
    <div className="col m2 s3 l2 mt-4 hide-on-small-and-down">
      <div className="ml-2">
        <p style={{ fontWeight: '700', fontSize: '1.2rem' }}>
          Avg. Customer rating{' '}
        </p>
        <Stars totalStars={5} size={20} />
        <span style={{ fontWeight: '700', fontSize: '1.2rem' }}> & up</span>
        <form action="#" className="mt-2">
          <p>
            <label>
              <input
                id="indeterminate-checkbox"
                type="checkbox"
                name="cosmetics"
                onClick={onChange}
              />
              <span>Cosmetics</span>
            </label>
          </p>
          <p>
            <label>
              <input
                id="indeterminate-checkbox"
                type="checkbox"
                name="haircare"
                onClick={onChange}
              />
              <span>Haircare</span>
            </label>
          </p>
          <p>
            <label>
              <input
                id="indeterminate-checkbox options"
                type="checkbox"
                name="skincare"
                onChange={onChange}
              />
              <span>Skincare</span>
            </label>
          </p>
        </form>
      </div>
    </div>
  );
}

export default Options;
