import React, { useEffect, useState, useContext } from 'react';
import M from 'materialize-css';
import AlertContext from '../../../context/alert/alertContext';
import BasketContext from '../../../context/basket/basketContext';
import AuthContext from '../../../context/auth/authContext';
function QuantitySelector({ itemId }) {
  const authContext = useContext(AuthContext);
  const { user } = authContext;
  const basketContext = useContext(BasketContext);
  const { editBasketItemQuantity } = basketContext;
  const alertContext = useContext(AlertContext);
  const { setAlert } = alertContext;
  useEffect(() => {
    var elems = document.querySelectorAll('select');
    M.FormSelect.init(elems);
  }, []);
  const [quantity, setQuantity] = useState(1);
  useEffect(() => {
    if (quantity === 0) {
      setQuantity(1);
      setAlert(
        'Quantity can not be set to 0',
        'blue darken-2',
        'col s10 offset-s1 m10 offset-m1 l10 offset-l1'
      );
    } else {
      editBasketItemQuantity(itemId, quantity);
      console.log('Fired');
    }
  }, [quantity]);

  const decreaseQuantity = () => {
    setQuantity(quantity - 1);
  };
  const increaseQuantity = () => {
    setQuantity(quantity + 1);
  };
  return (
    <div className="basket-increment">
      <button
        className="basket-decrement_button"
        name="decrease"
        onClick={decreaseQuantity}
      >
        -
      </button>{' '}
      <input type="text" value={quantity} readOnly />
      <button
        className="basket-increment_button"
        name="increase"
        onClick={increaseQuantity}
      >
        +
      </button>
    </div>
  );
}

export default QuantitySelector;
