import React, { useEffect, useContext, Fragment } from 'react';
import MainPageItemList from './MainPageItemList';
import Options from '../selector/Options';
import Pagination from '../utils/Pagination';
import AuthContext from '../../context/auth/authContext';
import ProductContext from '../../context/products/productContext';
import Search from '../selector/Search';
function MainPageItems() {
  const authContext = useContext(AuthContext);
  const productContext = useContext(ProductContext);
  const { persistUser, isAuthenticated } = authContext;
  const { getProducts, products, category } = productContext;
  useEffect(() => {
    if (!isAuthenticated) {
      persistUser();
    }

    getProducts();
  }, []);
  return (
    <div className="z-depth-1 grey lighten-3">
      <Search />
      <div className="row">
        <Options />
        <div className="col s10 offset-s1 m10 l10">
          {category}
        </div>

        <Pagination />
      </div>
    </div>
  );
}

export default MainPageItems;
