import {
  GET_BASKET,
  DELETE_BASKET_ITEM,
  CREATE_CHECKOUT_SESSION,
  BASKET_ERROR,
  ADD_TO_BASKET,
  GET_BASKET_TOTAL,
} from '../types';
import React, { useReducer } from 'react';
import BasketContext from './basketContext';
import basketReducer from './basketReducer';
import axios from 'axios';
axios.defaults.withCredentials = true;

const BasketState = (props) => {
  const initialState = {
    basket: null,
    sessionId: null,
    total: 0,
    error: null,
  };

  const [state, dispatch] = useReducer(basketReducer, initialState);

  //Get basket items
  const getBasketItems = async () => {
    try {
      const res = await axios.get(`/api/v1/basket/getitems`);

      dispatch({ type: GET_BASKET, payload: res.data.data });
    } catch (error) {
      dispatch({ type: BASKET_ERROR, payload: error.response.data });
    }
  };
  //Get basket total
  const getBasketTotal = async (id, quantity) => {
    const item = {
      id,
      quantity,
    };
    dispatch({ type: GET_BASKET_TOTAL, payload: item });
  };

  //Delete item from basket
  const deleteBasketItem = async (id) => {
    try {
      await axios.delete(`/api/v1/basket/deleteitem/${id}`);
      dispatch({ type: DELETE_BASKET_ITEM, payload: id });
    } catch (error) {
      dispatch({ type: BASKET_ERROR, payload: error.response.data });
    }
  };
  //Create stripe checkout session
  const createCheckoutSession = async (total, basket) => {
    const body = {
      total,
      basket,
    };
    const config = {
      headers: {
        'Content-Type': 'application/json',
      },
    };
    try {
      const res = await axios.post(
        '/api/v1/basket/create-checkout-session',
        products,
        config
      );
      dispatch({ type: CREATE_CHECKOUT_SESSION, payload: res.data.id });
    } catch (error) {
      dispatch({ type: BASKET_ERROR, payload: error.response.data });
    }
  };
  //Add item to users' basket
  const addToBasket = async (product, quantity) => {
    let item = {
      product: product._id,
      quantity,
    };
    const config = {
      headers: {
        'Content-Type': 'application/json',
      },
    };

    try {
      await axios.put(
        `/api/v1/basket/${product._id}/addtobasket`,
        item,
        config
      );

      dispatch({ type: ADD_TO_BASKET, payload: product });
    } catch (error) {
      dispatch({ type: BASKET_ERROR, payload: error.response.data });
    }
  };
  return (
    <BasketContext.Provider
      value={{
        basket: state.basket,
        sessionId: state.sessionId,
        error: state.error,
        total: state.total,
        getBasketItems,
        deleteBasketItem,
        getBasketTotal,
        addToBasket,
        createCheckoutSession,
      }}
    >
      {props.children}
    </BasketContext.Provider>
  );
};

export default BasketState;
