import React, { useContext } from 'react';
import AuthContext from '../../../context/auth/authContext';
import AlertContext from '../../../context/alert/alertContext';
export default function EditDeliveryAddress() {
  const authContext = useContext(AuthContext);
  const alertContext = useContext(AlertContext);
  const { setAlert } = alertContext;
  const {} = authContext;
  const [updateAddress, setUpdateAddress] = useState({
    line1: line1 || '',
    line2: line2 || '',
    city: city || '',
    postCode: postCode || '',
  });
  const onSubmit = (e) => {
    e.preventDefault();

    if (line1 === '' || city === '' || postCode === '') {
      setAlert(
        'Please provide all required details',
        'red',
        'col s8 offset-s2 m8 offset-m2 l8 offset-l2'
      );
    } else {
      updateUserAddress({ updateAddress });
    }
  };
  const onChange = (e) => {
    setUpdateAddress({ ...updateAddress, [e.target.name]: e.target.value });
  };
  return (
    <div className="row">
      <form className="col s12">
        <div className="row">
          <div className="input-field col s6">
            <i className="material-icons prefix">account_circle</i>
            <input
              id="first_name"
              type="text"
              className="validate"
              onChange={onChange}
              value={name}
              name="line1"
            />
            <label htmlFor="first_name" className="active">
              Address line 1 (or Company name)
            </label>
          </div>
          <div className="input-field col s6">
            <i className="material-icons prefix">account_circle</i>
            <input
              id="last_name"
              type="text"
              className="validate"
              onChange={onChange}
              value={lastName}
              name="line2"
            />
            <label htmlFor="last_name" className="active">
              Address line 2 (optional)
            </label>
          </div>
          <div className="input-field col s6">
            <i className="material-icons prefix">email</i>
            <input
              id="email"
              type="email"
              className="validate"
              onChange={onChange}
              value={email}
              name="city"
            />
            <label htmlFor="email" className="active">
              Town/City
            </label>
          </div>
          <div className="input-field col s6">
            <i className="material-icons prefix">phone</i>
            <input
              id="icon_telephone"
              type="tel"
              className="validate"
              onChange={onChange}
              value={tel}
              name="tel"
            />
            <label htmlFor="icon_telephone" className="active">
              Postcode
            </label>
          </div>
        </div>

        <button
          className="btn waves-effect waves-light blue lighten-1"
          type="submit"
          onClick={onSubmit}
        >
          Submit
          <i className="large material-icons right ">send</i>
        </button>
      </form>
    </div>
  );
}
