import React, { useReducer, useState } from 'react';
import EditDeliveryAddress from './EditDeliveryAddress';
function DeliveryAddress({ address }) {
  const [form, toggleForm] = useReducer((form) => !form, false);
  let editAddress = {};
  const setPrimary = (e) => {
    toggleForm();
    if (address.length !== 0) {
      editAddress = address.filter(address.rank === e.target.name)[0];
    } else {
      editAddress;
    }
  };
  const setSecondary = (e) => {
    toggleForm();
    if (address.length !== 0) {
      editAddress = address.filter(address.rank === e.target.name)[0];
    }
  };
  return (
    <div id="test6">
      {form ? (
        <EditDeliveryAddress toggleForm={toggleForm} address={editAddress} />
      ) : (
        <div className="row">
          <div className="col s8 offset-s2 m6 l6">
            <div className="card-panel grey lighten-3">
              <span className="black-text">
                <table>
                  <thead>
                    <tr>
                      <th>Primary</th>
                    </tr>
                  </thead>

                  <tbody>
                    <tr>
                      <td>
                        {address?.[0]
                          ? address[0].line1
                          : 'In order to expedite the checkout process please set up the primary delivery address'}
                      </td>
                    </tr>
                  </tbody>
                </table>
              </span>{' '}
              <button
                className="btn waves-effect waves-light light-blue mt-3"
                onClick={setPrimary}
                name="Primary"
              >
                Edit
                <i className="material-icons right">edit</i>
              </button>
            </div>
          </div>
          <div className="col s8 offset-s2 m6 l6">
            <div className="card-panel grey lighten-4">
              <span className="black-text">
                <table>
                  <thead>
                    <tr>
                      <th>Secondary</th>
                    </tr>
                  </thead>

                  <tbody>
                    <tr>
                      <td>
                        {address.length > 1
                          ? address[1]
                          : 'No secondary address provided'}
                      </td>
                    </tr>
                  </tbody>
                </table>
              </span>
              <button
                className="btn waves-effect waves-light light-blue mt-3"
                onClick={setSecondary}
                name="Secondary"
              >
                Edit
                <i className="material-icons right">edit</i>
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default DeliveryAddress;
