import React, { Fragment, useEffect, useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import AuthContext from '../../../context/auth/authContext';
import ProductContext from '../../../context/products/productContext';
import AlertContext from '../../../context/alert/alertContext';
import BasketContext from '../../../context/basket/basketContext';
import AddImages from '../../admin/AddImages';
import Stars from '../../selector/starRating/Stars';
function ItemCard({ itemId }) {
  const alertContext = useContext(AlertContext);
  const basketContext = useContext(BasketContext);
  const { addToBasket } = basketContext;
  const { setAlert } = alertContext;
  const authContext = useContext(AuthContext);
  const productContext = useContext(ProductContext);
  const { role } = authContext;
  const { product, loading, addImages } = productContext;

  let url = '';

  const [image, setImage] = useState(false);

  useEffect(() => {
    setImage(false);
  }, [addImages]);
  const showImageInput = () => {
    setImage(true);
  };
  const toBasket = (e) => {
    e.preventDefault();
    setAlert(
      'Item succesfully added to basket',
      'green',
      'col s8 offset-s2 m8 offset-m2 l8 offset-l2'
    );
    addToBasket(itemId);
  };

  return (
    <Fragment>
      {image && <AddImages id={itemId} />}

      <div className="row">
        <div className="col m12 s12 l12">
          <h2 className="header center">{product && product.name}</h2>
          <div className="card horizontal">
            <div className="card-stacked">
              <div>
                <div className="row">
                  <div className="col m6 s6 l6">
                    <div className="card-content">
                      <p className="">{product && product.description}</p>
                    </div>
                  </div>
                  <div className="col m6  mt-1 s6">
                    <div className="item-view-stars center">
                      <div>
                        <span>Avarge rating:</span>
                      </div>
                      <Stars size="18" />
                    </div>
                  </div>
                </div>
              </div>
              <div className="container">
                <div className="row">
                  <div className="col m4">
                    <div className="card-action center">
                      <a href="#one!" onClick={toBasket}>
                        Add to basket
                      </a>
                    </div>
                  </div>
                  <div className="col m3">
                    <div className="card-action center">
                      <a href={url}>Add to wish list</a>
                    </div>
                  </div>
                  <div className="col m3">
                    <div className="card-action center">
                      <a href={url}>Ask the seller</a>
                    </div>
                  </div>
                  {role === 'admin' && (
                    <div className="col m3">
                      <div className="card-action">
                        <div className="row">
                          <div className="input-field col s4 m4 l4 offset-s1 offset-m1 offset-l1">
                            <button
                              className="btn waves-effect waves-light blue lighten-1"
                              onClick={showImageInput}
                            >
                              Add Images
                              <i className="large material-icons right">send</i>
                            </button>
                          </div>
                        </div>{' '}
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </Fragment>
  );
}

export default ItemCard;
