import React, { useEffect, useState, useContext } from 'react';
import M from 'materialize-css';
import e from 'express';
function AddMerchandise() {
  useEffect(() => {
    const elem = document.getElementById('textarea2');
    M.CharacterCounter.init(elem);

    var elems = document.querySelectorAll('select');
    M.FormSelect.init(elems);
  }, []);
  const [product, setProduct] = useState({
    name: '',
    price: '',
    quantity: '',
    category: '',
  });
  const [images, setImages] = useState('');
  const onChange = (e) => {
    setProduct({ ...product, [e.target.name]: e.target.value });
  };
  const imageUpdate = () => {
    setProduct({ ...product, [e.target.name]: e.target.value });
  };

  const onSubmit = (e) => {};
  return (
    <div className="row">
      <form className="col s12 m12 l12 mt-4">
        <div className="input-field col s4 m4 l4 offset-s1 offset-m1 offset-l1">
          <input
            id="first_name"
            type="text"
            className="validate"
            name="name"
            value={name}
            onChange={onChange}
          />
          <label htmlFor="first_name">Name</label>
        </div>
        <div className="input-field col s4 m4 l4 offset-s1 offset-m1 offset-l1">
          <input
            id="price"
            type="text"
            className="validate"
            name="price"
            value={price}
            onChange={onChange}
          />
          <label htmlFor="price">Price in GPB</label>
        </div>
        <div className="input-field col s4 m4 l4 offset-s1 offset-m1 offset-l1">
          <input
            id="quantity"
            type="text"
            className="validate"
            name="quantity"
            value={quantity}
            onChange={onChange}
          />
          <label htmlFor="quantity">Quantity</label>
        </div>
        <div className="input-field col s4 m4 l4 offset-s1 offset-m1 offset-l1">
          <select>
            <option value="" disabled selected>
              Choose Category
            </option>
            <option value={category} name="cosmetics">
              Option 1
            </option>
            <option value={category} name="skincare">
              Option 2
            </option>
            <option value={category} name="perfume">
              Option 3
            </option>
          </select>
          <label>Merhcandise Category</label>
        </div>
        <div className="row">
          <div className="input-field col s4 m4 l4 offset-s1 offset-m1 offset-l1">
            <textarea
              id="textarea2"
              className="materialize-textarea"
              data-length="1000"
            ></textarea>
            <label htmlFor="textarea2">Description</label>
          </div>
          <div className="file-field input-field col s4 m4 l4 offset-s1 offset-m1 offset-l1">
            <div className="btn">
              <span>Images</span>
              <input type="file" multiple />
            </div>
            <div className="file-path-wrapper">
              <input
                className="file-path validate"
                type="text"
                placeholder="Upload one or more files"
                value={images}
                name="images"
                onChange={imageUpdate}
              />
            </div>
          </div>
        </div>{' '}
        <div className="row">
          <div className="input-field col s4 m4 l4 offset-s1 offset-m1 offset-l1">
            <button
              className="btn waves-effect waves-light blue lighten-1"
              type="submit"
              onClick={onSubmit}
            >
              Submit
              <i className="large material-icons right ">send</i>
            </button>
          </div>
        </div>{' '}
      </form>
    </div>
  );
}

export default AddMerchandise;
